<!-- FULL_CODE_START
print("=== Defense in Depth Simulation ===\n")

class SecurityLayer:
    def __init__(self, name):
        self.name = name

    def check(self, packet):
        raise NotImplementedError

class Firewall(SecurityLayer):
    def check(self, packet):
        if packet['port'] not in [80, 443]:
            print(f"üõë {self.name}: Blocked traffic on port {packet['port']}")
            return False
        print(f"‚úÖ {self.name}: Port {packet['port']} allowed")
        return True

class IDS(SecurityLayer):
    def check(self, packet):
        if "SELECT * FROM" in packet['payload'].upper():
            print(f"üõë {self.name}: Detected SQL Injection signature!")
            return False
        print(f"‚úÖ {self.name}: No known signatures found")
        return True

class Antivirus(SecurityLayer):
    def check(self, packet):
        if "virus" in packet['payload']:
            print(f"üõë {self.name}: Malware signature detected!")
            return False
        print(f"‚úÖ {self.name}: Clean")
        return True

# Define the stack
defense_stack = [
    Firewall("Perimeter Firewall"),
    IDS("Network IDS"),
    Antivirus("Endpoint AV")
]

def simulate_attack(packet_name, packet_data):
    print(f"\n--- Simulating: {packet_name} ---")
    for layer in defense_stack:
        if not layer.check(packet_data):
            print(f"üõ°Ô∏è Attack stopped at {layer.name}!")
            return
    print("‚ö†Ô∏è Attack penetrated all layers!")

# Sceanrios
normal_traffic = {'port': 443, 'payload': "GET /index.html"}
simulate_attack("Legitimate Web Request", normal_traffic)

bad_port = {'port': 22, 'payload': "ssh root@host"}
simulate_attack("SSH Brute Force", bad_port)

sql_injection = {'port': 80, 'payload': "admin' OR 1=1; SELECT * FROM users --"}
simulate_attack("Web SQL Injection", sql_injection)
FULL_CODE_END -->
